    zip.Encryption= EncryptionAlgorithm.WinZipAes256;
    zip.Password= "Top.Secret.No.Peeking!";
    zip.AddFile("7440-N49th.png");
    zip.AddFile("2008-Regional-Sales-Report.pdf");
    zip.Save("EncryptedArchive.zip");
