using (new SKAutoCanvasRestore(canvas)) {
    // perform some transform
    canvas.RotateDegrees(45);

    // draw as usual
    var paint = new SKPaint ();
    canavs.DrawRect (10, 10, 100, 100, paint);

    // automatically restore to original transform
}

